import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import {IAuthState, IUserState, localUser, market} from './../Models/index';


type initialState = WritableDraft<{
    auth: IAuthState;
    user: IUserState | null;
    localUser: localUser;
    market: never[];
}>;

export const authSlice = createSlice({
    name: 'auth',
    initialState: initialState,
    reducers: {
        setAuth: (state, action: PayloadAction<IAuthState>) => {
            state.auth = action.payload;
        },
        setUser: (state, action: PayloadAction<IUserState>) => {
            state.user = action.payload;
        },
        logout: state => {
            state.auth = {
                isLogged: false,
                token: null,
                refreshToken: null,
                refreshTokenExpiredDate: null,
                type: null,
            };
            state.user = null;
        },

        setLocalUser(state,action: PayloadAction<localUser>){
            state.localUser=action.payload;
        },
        addProduct: (state, action :PayloadAction<market>) => {
            return [...state, action.payload];
        },
    }

});

export const { setAuth ,addProduct} = authSlice.actions;

export const authReducer =  authSlice.reducer;
