import React from 'react';
import {Button, ListGroup, Modal} from 'react-bootstrap';
import {useSelector} from "react-redux";
import {RootState} from "../../store/reduxProvider";
interface props {
    visible : boolean,
    closeModal : ()=>void;
}


function ModalMarket(props:props) {
    const logged = useSelector((state: RootState)=>state.auth.auth.isLogged);
    const SanPham = useSelector((state:RootState)=>state.auth.Products)
    // const SanPhamMoke=[
    //     {nameArrival: 'aodai', cost: 100},
    //     {nameArrival: 'so mi', cost: 100},
    //     {nameArrival: 'giay', cost: 100},
    //     {nameArrival: 'quan bo', cost: 100},
    //     {nameArrival: 'aodai', cost: 100},
    //     {nameArrival: 'aodai', cost: 100},
    //
    // ]
    const totalCost = SanPham.reduce((total, item) => total + item.price, 0);

    return (

                <Modal show={props.visible} animation={true} style={styled.container} scrollable={true}>

                    <div style={{textAlign: 'center'}}>
                        { logged?
                            <div >
                                <div>
                                    <h1>Giỏ Hàng</h1>
                                    <ul>
                                        {
                                            SanPham.map((data)=>(
                                                <ListGroup horizontal={true} className="my-2">
                                                    <ListGroup.Item  style={{display: "flex",justifyContent: 'space-between',width: 450}}>
                                                        <p >Tên sản phẩm : {data.name}</p>
                                                        <p>Giá : {data.price}</p>
                                                    </ListGroup.Item>
                                                </ListGroup>

                                                ))}
                                    </ul>
                                    <div>
                                        <p> Tổng giá tiền: {totalCost}</p>
                                    </div>

                                </div>
                            </div>
                            :
                            <div>
                                <div></div>
                                <button>
                                    Đăng Nhập
                                </button>
                            </div>

                        }
                    </div>
                </Modal>

    );
}
const  styled={
    container:{borderRadius:10},
    buttonClose:{
        // height: 20,
        padding:10,
        borderRadius: 10,
    }
}
export default ModalMarket;
