import { createSlice, PayloadAction } from '@reduxjs/toolkit';
import {IAuthState, IUserState, localUser} from './../Models/index';

interface Product {
    name: string;
    price: number;
}

interface AppState {
    auth: IAuthState;
    user: IUserState | null;
    localUser: localUser;
    products: Product[];
}

const initialState: AppState = {
    auth: {} as IAuthState,
    user: null,
    localUser: {} as localUser,
    products: [],
};

export const authSlice = createSlice({
    name: 'auth',
    initialState: {
        auth: {} as IAuthState,
        user: null,
        localUser: {} as localUser,
        products: [],
    },
    reducers: {
        setAuth: (state, action: PayloadAction<IAuthState>) => {
            state.auth = action.payload;
        },
        // setUser: (state, action: PayloadAction<IUserState>) => {
        //     state.user = action.payload;
        // },
        logout: state => {
            state.auth = {
                isLogged: false,
                token: null,
                refreshToken: null,
                refreshTokenExpiredDate: null,
                type: null,
            };
            state.user = null;
        },

        setLocalUser(state,action: PayloadAction<localUser>){
            state.localUser=action.payload;
        },
        addProduct: (state,action:PayloadAction<Product>) => {
            // state.products.push(action.payload)
            console.log('gia tri cuuuu',state.products )
            state.products.push({
                name: action.payload.name,
                price: action.payload.price
            })
            console.log('gia tri  cap nhatttt', action.payload)
        },
    }

});

export const { setAuth, addProduct } = authSlice.actions;

export const authReducer =  authSlice.reducer;
